import React from 'react'
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import BigTeamCard from '../../components/BigTeamCard/BigTeamCard'
import Calendar from '../../components/Calendar/Calendar'
function Teams(props) {
    const GET_TEAM = gql`
    query team($name: String){
        team(name: $name){
          name
          city
          teamRecord{
            wins
            losses
            ot
            gamesPlayed
            goalsScored
            goalsAgainst
            streakType
            streakLength
            points
          }
        }
      }
        `
    const GET_SCHED = gql`
    query allGames($name: String){
      allGames(name: $name){
        homeTeam{
          city
          name
        }
        awayTeam{
          city
          name
        }
        date
        venue
        gamePk
        status
        finalScoreHome
        finalScoreAway
      }
    }
        `      
        const { data:schedData, loading:schedLoading, error:schedError } = useQuery(GET_SCHED, {variables: {name: props.match.params.name}});
        if (schedLoading) return <div>Loading</div>
        if (schedError) return <div>Error</div>      
    const { data:teamData, loading:teamLoading, error:teamError } = useQuery(GET_TEAM, {variables: {name: props.match.params.name}});
    if (teamLoading) return <div>Loading</div>
    if (teamError) return <div>Error</div>



    return (
        <div>
            <BigTeamCard team={teamData.team}></BigTeamCard>
            <Calendar/>
        </div>
    )
}

export default Teams
