import React from 'react'
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';
import BigTeamCard from '../../components/BigTeamCard/BigTeamCard'
import ScheduleMonth from '../../components/ScheduleMonth/ScheduleMonth'
function Schedule(props) {
    const GET_TEAM = gql`
    query team($name: String){
        team(name: $name){
          name
          city
          teamRecord{
            wins
            losses
            ot
            gamesPlayed
            goalsScored
            goalsAgainst
            streakType
            streakLength
            points
          }
          homeGame{
            homeTeam{
              city
              name
            }
            awayTeam{
              city
              name
            }
            date
            venue
            gamePk
            status
            finalScoreHome
            finalScoreAway
            homeTeamStatus
            awayTeamStatus
          }
          awayGame{
            homeTeam{
              city
              name
            }
            awayTeam{
              city
              name
            }
            date
            venue
            gamePk
            status
            finalScoreHome
            finalScoreAway
          }
        }
    }
        `     
    const { data:teamData, loading:teamLoading, error:teamError } = useQuery(GET_TEAM, {variables: {name: props.match.params.name}});
    if (teamLoading) return <div>Loading</div>
    if (teamError) return <div>Error</div>
    const games_by_month = teamData
    return (
        <div>
            <BigTeamCard team={teamData.team}></BigTeamCard>
            <h2> Im in the Schedule Page!</h2>
            <ScheduleMonth games={teamData} month={'October'} />
        </div>
    )
}

export default Schedule
